  echo "[INFO] REPOSITORY: $GIT_URL"
  echo "[INFO] BRANCH: $GIT_BRANCH"
  echo "[INFO] ENVIRONMENT: $ENVIRONMENT"
  docker run --rm -v $(pwd)/.ssh:/root/.ssh -v $(pwd):/git alpine/git clone -b $GIT_BRANCH $GIT_URL
